CREATE DATABASE /*!32312 IF NOT EXISTS*/`kqc_web` /*!40100 DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci */;

USE `kqc_web`;

/*Table structure for table `artikel` */

DROP TABLE IF EXISTS `artikel`;

CREATE TABLE `artikel` (
  `artikel_id` int(5) NOT NULL AUTO_INCREMENT,
  `kategori_id` int(5) DEFAULT NULL,
  `artikel_username` varchar(30) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_judul` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_judul_seo` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_isi` text COLLATE utf8_unicode_ci,
  `artikel_hari` varchar(20) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_tanggal` datetime DEFAULT NULL,
  `artikel_gambar` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_view` int(5) DEFAULT '1',
  `artikel_tag` varchar(100) CHARACTER SET latin1 COLLATE latin1_general_ci DEFAULT NULL,
  `artikel_status` enum('Y','N') COLLATE utf8_unicode_ci DEFAULT 'N',
  PRIMARY KEY (`artikel_id`)
) ENGINE=MyISAM AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `artikel` */

insert  into `artikel`(`artikel_id`,`kategori_id`,`artikel_username`,`artikel_judul`,`artikel_judul_seo`,`artikel_isi`,`artikel_hari`,`artikel_tanggal`,`artikel_gambar`,`artikel_view`,`artikel_tag`,`artikel_status`) values (1,1,'Administrator','Syeikh Basith : Hikmah dari Bencana','syeikh-basith--hikmah-dari-bencana','<p>Syeikh Abdul Basith Musfi, pemuda 23 tahun yang lahir di Arab Saudi itu mengaku prihatin dan turut berduka atas sejumlah bencana alam yang terjadi di Indonesia. Khususnya <a href=\"https://sedekahonline.com/donasi/bantu-korban-banjir-bandang-konawe\">banjir bandang</a> di Konawe dan Konawe Utara, Sulawesi Tenggara pada awal Juni lalu.</p>\r\n\r\n<p>Namun menurut syiekh yang saat ini aktif menjadi pengajar di Univeritas Umul Qura Mekah itu, ada hikmah yang bisa dipetik umat muslim dari setiap bencana yang terjadi. Musibah sejatinya adalah ujian dari Allah agar ketakwaan umatNya semakin meningkat.</p>\r\n\r\n<p>“Hikmah dari bencana itu kita harus berlebih ibadah, dengan mengingat Allah, berzikir kepadaNya, membaca Al-Qur’an dan melakukan hal-hal yang membuat kita semakin meningkatkan ibadah kepadaNya,” ujar hafizh 30 juz itu.</p>\r\n\r\n<p>Kemudian tutur Syeikh Basith dengan ikut membantu saudara-saudara sesama muslim yang terkena dampak bencana tersebut. Ia pun menyebutkan hadits riwayat Bukhari dan Muslim, ” Tidak sempurna Iman seseorang sehingga ia mencintai saudaanya sebagaimana ia mencintai dirinya sendiri. ”</p>\r\n\r\n<p>“Orang yang beriman itu adalah ia yang membantu fakir, miskin dan mereka yang sedang kesulitan. Umat muslim yang membantu sesama itu pahalanya besar sekali. Mari biasakan berzikir, membaca Al-Qur’an, <a href=\"https://sedekahonline.com/donasi/indonesia-siaga-bencana\">sedekah</a> dan berinfak,” tutur Syeikh Basith.</p>','Selasa','2019-06-25 21:53:22','c1b7329257cbae933daf031cbd0d19fc.jpg',0,'sepakbola','Y'),(2,2,'Administrator','Ahmadinejad: Gaza Akan Jadi Kuburan Israel','ahmadinejad-gaza-akan-jadi-kuburan-israel','<p>Iran dan Israel tampaknya tidak akan pernah melakukan genjatan senjata. Presiden Iran Mahmoud Ahmadinejad melontarkan kata-kata serangan terhadap Israel dengan menyebut negara Yahudi itu akan segera lenyap dari bumi. \"<em>Kejahatan yang dilakukan rejim Zionis (Israel) terjadi karena mereka sadar sudah sampai di akhir dan segera lenyap dari muka bumi</em>,\" kata Ahmadinejad dalam pawai anti-Israel yang berlangsung di Teheran, seperti dilaporkan kantor berita Mehr dan dikutip DPA, Sabtu (13/12).<br>\r\n<br>\r\nDia mengatakan Israel sudah kehilangan arah dan kian sadar bahwa kelompok negara-negara kuat makin ragu untuk menunjukkan dukungan untuk negara Yahudi itu.<br>\r\n<br>\r\nAhmadinejad juga mengatakan bahwa kejahatan Israel di Gaza bertujuan mengganti pemimpin politik di wilayah itu agar sesuai dengan kepentingan politik Israel. (sumber: <a href=\"http://inilah.com\" target=\"_blank\">inilah.com</a>)</p>','Selasa','2019-06-25 22:14:15','3048eaeb330e4a27c005ebd9a5b615ac.jpg',0,'gaza,israel,palestina','Y'),(3,1,'Administrator','Google Chrome Susupi Microsoft','google-chrome-susupi-microsoft','<p>Browser Microsoft, Internet Explorer (IE), bisa mendominasi karena tersedia secara default pada banyak komputer di pasaran. Google Chrome akan menggoyang dengan menyusup di lahan yang sama. Google rupanya sudah bersiap-siap menawarkan Google Chrome secara default pada komputer-komputer baru.</p>\r\n\r\n<p>Pichai juga menjanjikan Chrome akan keluar dari versi Beta (uji coba) pada awal 2009. Jika Google berhasil menyusupkan Chrome dalam lahan yang selama ini jadi &#39;mainan&#39; Microsoft, lanskap perang browser akan mengalami perubahan.</p>\r\n\r\n<p>Saat ini Microsoft masih mendominasi pada kisaran 70 persen lewat Internet Explorer-nya, sedangkan Firefox menguasai sekitar 20 persen. (sumber: <a href=\"http://detikinet.com\" target=\"_blank\">detikinet.com</a>)</p>','Minggu','2019-06-23 19:32:59','8438de06e424ec745124638020936c00.jpg',0,'browser,google','Y'),(4,2,'Administrator','Pemilik Facebook akan Dibuat Filmnya','pemilik-facebook-akan-dibuat-filmnya','<p>Sutradara David Fincher nampak jeli melihat peluang di tengah booming fenomena Facebook. Fincher akan menghadirkan sebuah film yang menceritakan tentang Mark Zuckerberg dan Facebook bagi para pencinta film dan Facebook tentunya.</p>\r\n\r\n<p>Fincher mengaku rencana pembuatan film ini masih dinegosiasikan dengan pihak Zuckerberg. Dia hanya menyebutkan, filmya akan fokus menceritakan Mark Zuckerberg yang awalnya merancang Facebook sebatas untuk keperluan mahasiswa Universitas Harvard.</p>\r\n\r\n<p>Film ini memaparkan bagaimana setelah itu Facebook kemudian berkembang menjadi fenomena yang mendunia sejak diluncurkan pada 2004.</p>\r\n\r\n<p>Dalam penggarapan film ini, Fincher mengajak serta orang-orang kompeten di bidang film. Antara lain Aaron Sorkin, yang merupakan penulis naskah acara serial televisi ternama The West Wing.</p>\r\n\r\n<p>Sementara itu, Columbia Pictures yang menamai film ini \"The Social Network\" dipercaya untuk memulai produksi film pada akhir tahun ini.</p>\r\n\r\n<p>Sebagian orang menilai kehadiran film ini nantinya akan mengorek kembali kasus lama dimana tiga teman Zuckerberg, Cameron dan Tyler Winklevoss serta Divya Narendra mengklaim Zuckerberg telah mencuri ide mereka untuk membuat Facebook.</p>\r\n\r\n<p>Pada saat Zuckerberg meluncurkan Facebook, mereka menuntut perkara atas Zuckerberg. Awal tahun ini, pengadilan AS memutuskan Facebook harus membayar USD65 juta untuk melunasi perkara ini.</p>','Selasa','2019-06-25 22:14:04','54d0cc5f0a0904da04e0975a20c00725.jpg',0,'film','Y'),(5,1,'Administrator','Sharapova, Petenis Wanita Berpenghasilan Tertinggi','sharapova-petenis-wanita-berpenghasilan-tertinggi','<p>Petenis asal Rusia, Maria Sharapova dengan penghasilan 26 juta dolar AS merupakan petenis wanita berpenghasilan tertinggi. Ia pernah menempati peringkat satu dunia, pasca mundurnya Justine Henin. Ia juga memiliki prestasi dengan menjuarai turnamen grand slam Australia Terbuka dan AS Terbuka. Namun, sebagian besar penghasilannya didapat dari kontrak iklannya bersama Pepsi, Colgate-Palmolive, Nike dan Motorola.<br>\r\n<br>\r\nBerikutnya disusul Williams bersaudara dari Amerika, yaitu Serena Williams dengan penghasilan 14 juta dolar AS. Ia meraih tiga gelar juara tiap tahunnya semenjak tahun 2005. Ia juga merupakan model dari produk Hawlett-Packard, Nike, dan Kraft. Sedangkan kakak kandungnya, yaitu Venus Williams berpenghasilan 13 juta dolar AS. Ia mengalahkan adiknya di final Wimbledon tahun 2008. Ia memiliki dan menjalankan sendiri usaha fashion Eleven.<br>\r\n<br>\r\nDi peringkat ke empat dan kelima adalah petenis Belgia yaitu Justine Henin dengan penghasilan 12,5 juta dolar AS. Dan petenis asal Serbia, yaitu Ana Ivanovic dengan penghasilan 6,5 juta dolar AS.</p>','Minggu','2019-06-23 19:35:53','67204b14e2d1ff9a9768b943ab5610b1.jpg',0,'tenis','Y'),(6,1,'admin','Dalam Dua Pekan, KCB 2 Ditonton 1,5 Juta','dalam-dua-pekan-kcb-2-ditonton-15-juta-penonton','<p>\r\nFilm Ketika Cinta Bertasbih (KCB) 2 diyakini bakal mereguk sukses seperti sekuel pertamanya Sejak diputar perdana tanggal 17 September lalu atau selama 15 hari, film garapan SinemArt telah disaksikan 1,5 juta penonton. \r\n</p>\r\n<p>\r\nRekor yang sama juga dialami KCB 1. &quot;Pada pemutaran KCB 1 kami bisa memecah rekor pemutaran film di Indonesia, yaitu mendapat penonton sebanyak 100.000 perhari,&quot; ungkap Frans dari SinemArt saat promo film KCB 2 di Royal Plaza, Minggu (4/10).\r\n</p>\r\n<p>\r\nPihak SinemArt berharap KCB 2 bisa meraih prestasi minimal sama dengan KCB 1 dengan total 3 juta penonton. Untuk mencapai target tersebut, pihak SinemArt tak henti melakukan serangkaian promo di sejumlah kota di Tanah Air maupun di mancanegara.\r\n</p>\r\n<p>\r\n&quot;Hari ini (Minggu, 4/10), Kholidi (Kholidi Asadil Alam, pemeran Azzam) dan Oki (Oki Setiana Dewi pemeran Anna) ke Hongkong untuk promo di sana,&quot; imbuh Frans. Pekan depan (10-12 Oktober 2009), giliran Meyda Sefira berangkat ke Makau untuk kegiatan yang sama.\r\n</p>\r\n<p>\r\nFilm besutan sutradara Chaerul Umam ini juga dijadwalkan diputar di Aceh pada tanggal 11-12 Oktober mendatang. Menurut Frans, pemutaran KCB 1 di kota yang dikenal dengan sebutan Serambi Mekkah ini ditonton 8.000 orang.\r\n</p>\r\n<p>\r\nPadahal di kota tersebut sama sekali tidak ada gedung bioskop. Karena itu kru SinemArt terpaksa mengusung peralatan khusus dari Jakarta dan memutar di sebuah gedung khusus selama dua hari dalam tujuh kali show.\r\n</p>\r\n<p>\r\nBertutur tentang kesan berperan di KCB 2, Kholidi beberapa waktu lalu mengaku paling terkesan dengan adegan kecelakaan saat membonceng Bu&#39;e (Ninik L Karim). Karena ketika sepeda motornya terjatuh dia harus teriak memanggil ibundanya. &quot;Bu&#39;eee! Wah itu lumayan sulit,&quot; ungkap Kholidi.\r\n</p>\r\n<p>\r\nAdegan lain yang cukup berkesan adalah ketika pria asal Pasuruan ini terkapar di rumah sakit paska kecelakaan yang dia alami. &quot;Ekspresi orang sakitnya kan harus dapat. Terus suaranya juga harus disesuaikan, tidak seperti kita ngomong biasa, jadi agak sedikit tertahan di tenggorokan, powernya tidak full seperti ngomong biasanya,&quot; bebernya.\r\n</p>\r\n<p>\r\nUntuk adegan itu Kholidi yang kini menempuh pendidikan di Universitas Al Azhar, Jakarta melakukan observasi pada beberapa orang yang pernah mengalami kecelakaan. &quot;Aku juga tanya-tanya ke dokter. Ternyata di tempat tidurnya nggak bisa pakai bantal, posisi badannya harus lurus. Terus kalau ada gips di kaki, posisi jalan kita akan seperti apa. Biar nantinya terlihat lebih reel lah adengannya,&quot;  pungkas Kholidi. (sumber: <a href=\"http://surya.co.id\">surya.co.id</a>) \r\n</p>\r\n','Minggu','2009-10-25 00:00:00','54kcb2.jpg',55,'film','Y'),(7,1,'admin','Windows 7 Gantikan Windows Vista','windows-7-gantikan-windows-vista','<p>\r\nMicrosoft  ingin memudahkan rencana para administrator komputer yang akan bermigrasi ke Windows 7, namun sebuah tulisan di blog salam satu anggota tim Windows 7 berkata sebaliknya.\r\nSkenario uji coba terbaru menunjukkan, sebagian besar pengguna, proses upgrading akan menyulitkan, mengambil waktu kira-kira 30 menit. \r\n</p>\r\n<p>\r\nProsentasi terbesar pengguna menyebut, migrasi butuh waktu hingga 21 jam.\r\nSalah satu anggota tim Windows dari Microsoft, Chris Hernandez, mengungkap hasi pengetesan timnya dengan berbagai merek komputer dan konfigurasi tipikal pengguna lewat simulasi pada tingkatan berbeda dari proses migrasi Vista ke Windwos pada Jumat akhir pekan lalu.\r\n</p>\r\n<p>\r\nTujuan simulasi untuk memastikan apakah upgrade dari Vista Service Pack (SP) 1 ke Windows 7, dalam lima persen percobaan utama, lebih cepat ketimbang upgrade dari Vista SP1 ke Vista SP2, ujar Chris.\r\nProses dari Vista SP1 ke Vista SP2 dipilih karena itu opsi instalasi paling umum digunakan Microsoft Product Support Services, yakni skenario repair (perbaikan ulang) di mana prosedur yang paling dianjurkan adalah melakukan re-instalasi sistem operasi (OS) yang sudah ada di komputer saat itu. \r\n</p>\r\n<p>\r\nChris menampilkan hasil tes dalam blognya.\r\nTim mengetes konfigurasi komputer khusus hadware, merentang dari kategori hardware low-end (spesifikasi rendah), mid-range (spesifikasi menengah) dan high-end (spesifikasi atas). Kategori itu berlawanan dengan skenario pengguna pada umumnya yang berbasis pertanyaan seperti, berapa besar set data yang dibutuhkan pengguna dan bagaimana macam aplikasi tersebut diinstall.\r\n</p>\r\n<p>\r\nUntuk kategori komputer spesifikasi atas, Chris dan timnya mendefinisikan komputer dengan sistem operasi 32 bit dan memiliki CPU berprosesor Inter Core 2 Quad, yang bejalan di 2,4 GHz, memori 4GB dan hardisk 1 Terabyte .\r\nSementara, pengguna umumnya memiliki data sebesar 125 GB yang terikat dalam format dokumen, musik dan gambar dengan 40 aplikasi yang diinstal di komputer mereka.\r\nKinerja upgrade Vista SP1 ke Windows 7 pada hardware spesifikasi atas dengan konfigurasi pemilik pengguna kelas berat, membutuhkan 160 menit, atau sekitar 2,7 jam. \r\n</p>\r\n<p>\r\nSebagai perbandingan, upgrade repair (perbaikan) dari Vista SP1 ke Vista SP1 dengan hadware yang sama dan penggunaan bera membutuhkan 176 menit, atau 2,9 jam.\r\nSkenario terburuk muncul pada konfigurasi hadware kelas menengah, yakni CPU 32 bit namun dengan software dan konfigurasi &quot;pengguna super&quot;. Proses upgrading akan butuh waktu hingga 1.220 menit alias 20,3 jam. Padahal yang dianggap hadware kelas menengah, memiliki spesifikasi setara memory 2GB RAM, prosesor dual core, Athlon 64 X2, pada 2,6GHz dan hardisk 1 Terabyte.\r\n</p>\r\n<p>\r\nMereka yang dianggap pengguna super, memiliki profil lebih sadis dalam istilah penggunaan data, ketimbang pengguna kelas berat pada umumnya. Sebagai contoh, tim penguji menyebut pengguna super memiliki 650 GB data dan 40 aplikasi lebih yang terinstal dalam komputer mereka.\r\nLalu pada kelas rendah, pengguna medium, dengan 70 GB data dan 20 aplikasi, dengan memori sekitar 1 GB, prosesor 64 bit, AMD Athlon pada kecepatan 2,2 GHz, bakal butuh waktu bermigrasi sekitar 175 menit. Hardware yang lebih bertenaga, secara umum membutuhkan waktu instalasi lebih singkat.\r\n</p>\r\n<p>\r\nMicrosoft tidak selalu bisa mencapai target lima persen tujuan tim Chris yang telah dijanjikan. Dalam satu contoh, instalasi bersih (instalasi pertama pada komputer baru tanpa OS) Windows 7 pada hardware spesifikasi menengah membutuhkan 30 menit sementara instalasi bersih Vista SP1 butuhk 31 menit. Hanya saja, secara keseluruhan, tidak ada instalasi Windows 7 yang lebih lambat dibandingkan Vista.\r\n</p>\r\n<p>\r\nPertanyaan tersisa, apakah para toko dan ritel software akan mendengar rayuan Microsoft dan memutuskan hijrah ke Windows 7 lebih cepat? Tradisi yang berlaku, ritel IT akan cenderung menunggu Service Pack I sebelum mendatangkan versi terbaru Windows.\r\nWaktu yang akan menjadi sumber menentukan apakah kalangan profesional IT akan berpindah, sehingga Vista tak lagi menarik bagi ritel dan toko software. Jadi kehijrahan mereka ke Windows 7 dengan segera, menandakan pula, apakah para profesional IT suka dengan hasil pengujian waktu instal yang dilakukan Chris Hernandez.  internetnews/itz.\r\n</p>\r\n','Minggu','2009-10-25 00:00:00','19windows7.jpg',20,'komputer','Y'),(8,1,'wiro','Cristiano Ronaldo Pemain Sepakbola Terbaik 2008','cristiano-ronaldo-pemain-sepakbola-terbaik-2008','Cristiano Ronaldo akhirnya terpilih sebagai Pemain Terbaik Dunia versi FIFA, mengalahkan Lionel Messi (Barcelona), dan Fernando Torres (Liverpool). Penetapan itu diumumkan di Zurich, Swiss, Senin atau Selasa (13/1) dini hari. Ronaldo menjadi pemain pertama dari Premier League yang menerima penghargaan itu. Sebelumnya, dia juga terpilih sebagai Pemain Terbaik Eropa (Ballon d\'Or) dan baru saja dinobatkan sebagai Pemain Terbaik Dunia versi suporter.<br><br>Pemain Manchester United (MU) asal Portugal itu meraih 935 pemilih dari poling yang disebar ke seluruh dunia. Pemilihnya hanya dibatasi kapten tim dan pelatih. Hasil itu diumumkan oleh pemain legendaris Brasil, Pele.<br><br>Sementara itu, Lionel Messi berada di tempat kedua. Pemain Barcelona asal Argentina itu meraih 678 suara. Adapun striker Liverpool asal Spanyol, Fernando Torres, berada di tempat ketiga dengan 203 suara.<br><br>Musim lalu, Ronaldo memang tampil bagus. Dia mencetak 42 gol dan membawa Manchester United merebut gelar Premier League dan Liga Champions.<br><br>\"Ini momen yang sangat indah buatku. Momen spesial dalam hidupku. Aku ingin mengatakan kepada ibu dan saudara perempuanku bahwa kembang api sudah saatnya disulut,\" kata Ronaldo seusai menerima penghargaan itu. (sumber: detiksport.com)<br>','Senin','2009-02-27 00:00:00','92cristianoronaldo.jpg',16,'sepakbola','Y'),(9,1,'admin','Text Editor keren untuk TextArea Ala Ms Word','editor-textarea-ala-ms-word','<div style=\"text-align: center\">\r\n</div>\r\n<div style=\"text-align: center\">\r\n</div>\r\n<div style=\"text-align: center\">\r\n</div>\r\n<p>\r\nSecara standar, textarea akan ditampilkan apa adanya, artinya teks yang diketik tidak bisa diatur formatnya, misalnya apabila kita ingin kalimat tertentu ditebalkan, dimiringkan atau diatur jenis dan ukuran hurufnya. Hal ini tidak bisa dilakukan dalam textarea standar, kecuali Anda hapal perintah HTML, kemudian menuliskannya secara manual di textarea tersebut, namun bagi reporter atau user yang awam tentu hal ini cukup menyulitkan mereka.<br />\r\n<br />\r\nSolusinya, gunakan editor <strong>WYSIWYG</strong> (<em>What You See Is What You Get</em>) &ndash; Apa yang kau lihat adalah apa yang kau dapatkan. Menurut pengertian dari Wikipedia, WYSIWYG adalah suatu editor yang memungkinkan user untuk menentukan format, ukuran dan jenis huruf, menambahkan hyperlink dan tabel, dan juga bisa mengupload file, gambar, animasi flash, dan video.<br />\r\n</p>\r\n<div style=\"text-align: center\">\r\n<img src=\"http://localhost./lokomedia/tinymcpuk/gambar/Image/cktini.jpg\" alt=\" \" width=\"326\" height=\"72\" />\r\n</div>\r\n<p>\r\nSaat ini banyak sekali editor WYSIWYG, tapi daripada bingung memilih, saya sarankan untuk menggunakan <strong>TinyMCE</strong> atau <strong>CKEditor</strong>, karena kedua open source editor WYSIWYG tersebut sudah teruji di CMS sekelas Joomla dan Wordpress. Alasan lainnya, karena kelengkapan dokumentasi, kaya fiturnya, kompatibilitas browser, dukungan forum, update, dan plugins. \r\n</p>\r\n<p>\r\nSaat searching di Google, saya ketemu sama yang namanya <strong>tinyFCK</strong> (<a href=\"http://p4a2.crealabsfoundation.org/tinyfck\" target=\"_blank\">http://p4a2.crealabsfoundation.org/tinyfck</a>), editor WYSIWYG yang menggabungkan kelebihan dari TinyMCE dan CKEditor, atau yang lebih kompleks lagi, yaitu <strong>TinyMCPUK</strong>, karena selain menggabungkan kelebihan dari TinyMCE dan CKEditor, juga ditambahkan image manager yang berguna untuk memanipulasi gambar.\r\n</p>\r\n','Selasa','2010-01-12 00:00:00','72office.jpg',159,'komputer','Y'),(10,2,'Administrator','Angry Birds Hadirkan Budaya Indonesia','angry-birds-hadirkan-budaya-indonesia','<p>Game mobile Angry Birds yang tengah digandrungi, rencananya akan masuk pasar Indonesia. Tim game Rovio ingin menggaet developer tanah air. Mereka mengaku mengharapkan kelahiran Angry Birds yang khas Indonesia.</p>\r\n\r\n<p>\"Kami mengerti (keinginan) itu. Siapa tahu nantinya Angry Birds ala Indonesia akan memakai batik,\" ungkap Chief Marketing Officer Rovio, Peter Vesterbacka, saat ditemui di Hotel Kempinski, Jakarta, Rabu (25/1). \"Tapi kami ingin menyelami lebih dalam. Apa saja yang bisa digali dari Indonesia.\"</p>\r\n\r\n<p>Peter juga mengungkapkan telah menerima banyak masukan untuk menghadirkan wayang, batik, garuda dan Bali di game tersebut. Untuk itu, Rovio bersama Nokia kemudian menggelar kompetisi. Diharapkan dari situ akan lahir Angry Birds yang \"Indonesia Banget\".</p>\r\n\r\n<p>Sayangnya, Peter enggan menyebutkan kapan tepatnya rencana itu akan direalisasikan. Namun ini tetap menjadi kabar baik untuk developer lokal untuk semakin berkibar. \"Pengembang aplikasi di Indonesia memiliki potensi dan talenta untuk dikembangkan,\" lanjut Peter yakin.</p>\r\n\r\n<p>(sumber: wowkeren.com)</p>','Selasa','2019-06-25 22:17:52','fbf2f4bfbcc4e618c51acb9faaa5a497.jpg',0,'film','Y'),(11,1,'Administrator','Hantam Laos 6-0, Indonesia Semifinal','hantam-laos-60-indonesia-semifinal','<p>?????? ?????? ??????????? ???????????</p>','Minggu','2019-06-23 00:00:00','1bachdim.jpg',0,'sepakbola','N'),(12,1,'Administrator','Pencari Kebahagiaan Sejati','pencari-kebahagiaan-sejati','<p>Syeikh Abdul Basith Musfi, pemuda 23 tahun yang lahir di Arab Saudi itu mengaku prihatin dan turut berduka atas sejumlah bencana alam yang terjadi di Indonesia. Khususnya <a href=\"https://sedekahonline.com/donasi/bantu-korban-banjir-bandang-konawe\">banjir bandang</a> di Konawe dan Konawe Utara, Sulawesi Tenggara pada awal Juni lalu.</p>\r\n\r\n<p>Namun menurut syiekh yang saat ini aktif menjadi pengajar di Univeritas Umul Qura Mekah itu, ada hikmah yang bisa dipetik umat muslim dari setiap bencana yang terjadi. Musibah sejatinya adalah ujian dari Allah agar ketakwaan umatNya semakin meningkat.</p>\r\n\r\n<p>“Hikmah dari bencana itu kita harus berlebih ibadah, dengan mengingat Allah, berzikir kepadaNya, membaca Al-Qur’an dan melakukan hal-hal yang membuat kita semakin meningkatkan ibadah kepadaNya,” ujar hafizh 30 juz itu.</p>\r\n\r\n<p>Kemudian tutur Syeikh Basith dengan ikut membantu saudara-saudara sesama muslim yang terkena dampak bencana tersebut. Ia pun menyebutkan hadits riwayat Bukhari dan Muslim, ” Tidak sempurna Iman seseorang sehingga ia mencintai saudaanya sebagaimana ia mencintai dirinya sendiri. ”</p>\r\n\r\n<p>“Orang yang beriman itu adalah ia yang membantu fakir, miskin dan mereka yang sedang kesulitan. Umat muslim yang membantu sesama itu pahalanya besar sekali. Mari biasakan berzikir, membaca Al-Qur’an, <a href=\"https://sedekahonline.com/donasi/indonesia-siaga-bencana\">sedekah</a> dan berinfak,” tutur Syeikh Basith.</p>','Selasa','2019-06-25 22:07:15','9f3635fe678db78e7b8f0ccf98de0994.jpg',0,'amerika,film','Y'),(13,1,'Administrator','Longsor, Pembangunan Masjid Sadaunta Terhambat','longsor-pembangunan-masjid-sadaunta-terhambat','<p>Gempa bumi dan tsunami yang terjadi di Sulawesi Tengah pada September 2018 lalu tak hanya menghancurkan rumah, pemukiman warga dan perkantoran. Bencana itu juga merusak jalan di sejumlah lokasi. Salah satunya akses menuju Kampung Qur’an Sadaunta di Kulawi, Kabupaten Sigi.</p>\r\n\r\n<p>Pascagempa  tanah menuju Sadaunta mudah bergeser. Apa lagi, curah hujan tinggi, akses berliku, jurang yang curam membuat longsor tak bisa terhindarkan dan jalan terputus. Hal itu menyebabkan supalai bahan bangunan terhambat. Aktivitas pembangunan Masjid Sadaunta pun tersendat.</p>\r\n\r\n<p>“Beberapa minggu lalu longsor kembali terjadi. Jalan ditutup selama satu pekan, sempat dibuka beberapa hari namun ditutup lagi dua minggu. Hal ini tentu membuat pengiriman bahan bangunan terkendala,” ujar Koordinator Siaga Bencana (SIGAB) PPPA Daarul Qur’an Mashuri.</p>\r\n\r\n<p>Sejatinya pembangunan Masjid Sadaunta sudah memasuki tahap akhir. Meski belum 100 persen rampung, masjid sudah digunakan warga untuk melaksanakan sholat wajib, tarawih dan ibadah lainnya pada Ramadhan 1440 H lalu.</p>\r\n\r\n<p>\"Tinggal pasang kubah dan pintu masjid, tapi jalan menuju lokasi putus, kubah dari Palu belum bisa sampai ke masjid. Kami memohon doa kepada seluruh masyarakat Indonesia khususnya donatur PPPA Daarul Qur’an agar pembangunan masjid dapat segera selesai,” harap Mashuri.</p>','Selasa','2019-06-25 21:50:51','473f6e8b2d2ba3dcaaa780f1c8d070b5.jpg',0,'browser,gaza','Y');

